SELECT * FROM astronauts;
SELECT COUNT(*) FROM astronauts;

-- What are average, max, and min values in the data?

SELECT
-- Calculate the average number of space flights.
  AVG(Space_Flights) AS Average_Space_Flights,
 -- Calculate the maximum number of space flights.
  MAX(Space_Flights) AS Max_Space_Flights,
 -- Calculate the minimum number of space flights.
  MIN(Space_Flights) AS Min_Space_Flights,
  
 -- Calculate the average total hours of space flight.
  AVG(Space_Flight_hr) AS Average_Space_Flight_hr,
 -- Calculate the maximum total hours of space flight.
  MAX(Space_Flight_hr) AS Max_Space_Flight_hr,
  -- Calculate the minimum total hours of space flight.
  MIN(Space_Flight_hr) AS Min_Space_Flight_hr,
  
 -- Calculate the average number of spacewalks.
  AVG(Space_Walks) AS Average_Space_Walks,
 -- Calculate the maximum number of spacewalks.
  MAX(Space_Walks) AS Max_Space_Walks,
   -- Calculate the minimum number of spacewalks.
  MIN(Space_Walks) AS Min_Space_Walks,
  
 -- Calculate the average total hours of spacewalks.
  AVG(Space_Walks_hr) AS Average_Space_Walks_hr,
 -- Calculate the maximum total hours of spacewalks.
  MAX(Space_Walks_hr) AS Max_Space_Walks_hr,
 -- Calculate the minimum total hours of spacewalks.
  MIN(Space_Walks_hr) AS Min_Space_Walks_hr
  
-- Specify the source table.
FROM astronauts;

-- What about those numbers per category in the data (using HAVING)?

SELECT
  AVG(Space_Flights) AS Average_Space_Flights,
  MAX(Space_Flights) AS Max_Space_Flights,
  MIN(Space_Flights) AS Min_Space_Flights,
  AVG(Space_Flight_hr) AS Average_Space_Flight_hr,
  MAX(Space_Flight_hr) AS Max_Space_Flight_hr,
  MIN(Space_Flight_hr) AS Min_Space_Flight_hr,
  AVG(Space_Walks) AS Average_Space_Walks,
  MAX(Space_Walks) AS Max_Space_Walks,
  MIN(Space_Walks) AS Min_Space_Walks,
  AVG(Space_Walks_hr) AS Average_Space_Walks_hr,
  MAX(Space_Walks_hr) AS Max_Space_Walks_hr,
  MIN(Space_Walks_hr) AS Min_Space_Walks_hr
FROM astronauts
-- Group the data by the "Status" category.
GROUP BY Status
-- Filter out rows where the average space flights are NULL (no data).
HAVING AVG(Space_Flights) IS NOT NULL;

-- What ways are there to group the data values that donâ€™t exist yet (using CASE)?

SELECT
  CASE
    WHEN Space_Flights IS NULL THEN 'No Space Flights'  -- Group NULL values
    WHEN Space_Flights = 1 THEN '1 Space Flight'
    WHEN Space_Flights = 2 THEN '2 Space Flights'
    ELSE 'More than 2 Space Flights'
  END AS Space_Flight_Category,
-- Calculate the count of astronauts in each category.
  COUNT(*) AS Astronaut_Count
FROM astronauts
-- Group the data by the "Space_Flight_Category."
GROUP BY Space_Flight_Category;


-- What interesting ways are there to filter the data (using AND/OR)?

-- Retrieve active female astronauts
SELECT *
FROM astronauts
WHERE Status = 'Active' AND Gender = 'Female';

-- Retrieve astronauts who joined after 2000 and have at least 2 space flights
SELECT *
FROM astronauts
WHERE Year > 2000 AND Space_Flights >= 2;

-- Retrieve astronauts who are either Colonels or have graduated from MIT
SELECT *
FROM astronauts
WHERE Military_Rank = 'Colonel' OR Alma_Mater LIKE '%MIT%';

-- Retrieve astronauts who have either conducted spacewalks or have more than 1000 hours of space flight
SELECT *
FROM astronauts
WHERE Space_Walks > 0 OR Space_Flight_hr > 1000;
